#' Boxplot
#'
#' 
#' @param data .
#' @param var .
#' @param group1 .
#' @param group2 .
#' @param flipAxes .
#' @param notch .
#' @param boxWidth .
#' @param outliers .
#' @param width .
#' @param height .
#' @param title .
#' @param titleAlign .
#' @param titleFontSize .
#' @param subtitle .
#' @param subtitleAlign .
#' @param subtitleFontSize .
#' @param caption .
#' @param captionAlign .
#' @param captionFontSize .
#' @param xLabel .
#' @param xLabelAlign .
#' @param xLabelFontSize .
#' @param yLabel .
#' @param yLabelAlign .
#' @param yLabelFontSize .
#' @param titleType .
#' @param yAxisLabelFontSize .
#' @param yAxisRangeType .
#' @param yAxisRangeMin .
#' @param yAxisRangeMax .
#' @param yAxisLabelRotation .
#' @param xAxisLabelFontSize .
#' @param xAxisLabelFontSizeRevLabels .
#' @param xAxisLabelRotation .
#' @param legendTitle .
#' @param legendTitleFontSize .
#' @param legendLabelFontSize .
#' @param legendKeyWidth .
#' @param legendKeyHeight .
#' @param legenPositionType .
#' @param legendPosition .
#' @param legendJustification .
#' @param legendPositionX .
#' @param legendPositionY .
#' @param legendDirection .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$plot} \tab \tab \tab \tab \tab an image containing the box plot \cr
#' }
#'
#' @export
jmvbox <- function(
    data,
    var,
    group1,
    group2,
    flipAxes = FALSE,
    notch = FALSE,
    boxWidth = 0.5,
    outliers = TRUE,
    width = 500,
    height = 500,
    title = "",
    titleAlign = "center",
    titleFontSize = 16,
    subtitle = "",
    subtitleAlign = "left",
    subtitleFontSize = 16,
    caption = "",
    captionAlign = "right",
    captionFontSize = 12,
    xLabel = "",
    xLabelAlign = "center",
    xLabelFontSize = 16,
    yLabel = "",
    yLabelAlign = "center",
    yLabelFontSize = 16,
    titleType = "title",
    yAxisLabelFontSize = 12,
    yAxisRangeType,
    yAxisRangeMin,
    yAxisRangeMax,
    yAxisLabelRotation = 0,
    xAxisLabelFontSize = 12,
    xAxisLabelFontSizeRevLabels = FALSE,
    xAxisLabelRotation = 0,
    legendTitle = "",
    legendTitleFontSize = 16,
    legendLabelFontSize = 16,
    legendKeyWidth = 0.6,
    legendKeyHeight = 0.6,
    legenPositionType = "outside",
    legendPosition = "right",
    legendJustification = "center",
    legendPositionX = 0.8,
    legendPositionY = 0.5,
    legendDirection = "vertical") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("jmvbox requires jmvcore to be installed (restart may be required)")

    if ( ! missing(var)) var <- jmvcore::resolveQuo(jmvcore::enquo(var))
    if ( ! missing(group1)) group1 <- jmvcore::resolveQuo(jmvcore::enquo(group1))
    if ( ! missing(group2)) group2 <- jmvcore::resolveQuo(jmvcore::enquo(group2))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(var), var, NULL),
            `if`( ! missing(group1), group1, NULL),
            `if`( ! missing(group2), group2, NULL))

    for (v in group1) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in group2) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- jmvboxOptions$new(
        var = var,
        group1 = group1,
        group2 = group2,
        flipAxes = flipAxes,
        notch = notch,
        boxWidth = boxWidth,
        outliers = outliers,
        width = width,
        height = height,
        title = title,
        titleAlign = titleAlign,
        titleFontSize = titleFontSize,
        subtitle = subtitle,
        subtitleAlign = subtitleAlign,
        subtitleFontSize = subtitleFontSize,
        caption = caption,
        captionAlign = captionAlign,
        captionFontSize = captionFontSize,
        xLabel = xLabel,
        xLabelAlign = xLabelAlign,
        xLabelFontSize = xLabelFontSize,
        yLabel = yLabel,
        yLabelAlign = yLabelAlign,
        yLabelFontSize = yLabelFontSize,
        titleType = titleType,
        yAxisLabelFontSize = yAxisLabelFontSize,
        yAxisRangeType = yAxisRangeType,
        yAxisRangeMin = yAxisRangeMin,
        yAxisRangeMax = yAxisRangeMax,
        yAxisLabelRotation = yAxisLabelRotation,
        xAxisLabelFontSize = xAxisLabelFontSize,
        xAxisLabelFontSizeRevLabels = xAxisLabelFontSizeRevLabels,
        xAxisLabelRotation = xAxisLabelRotation,
        legendTitle = legendTitle,
        legendTitleFontSize = legendTitleFontSize,
        legendLabelFontSize = legendLabelFontSize,
        legendKeyWidth = legendKeyWidth,
        legendKeyHeight = legendKeyHeight,
        legenPositionType = legenPositionType,
        legendPosition = legendPosition,
        legendJustification = legendJustification,
        legendPositionX = legendPositionX,
        legendPositionY = legendPositionY,
        legendDirection = legendDirection)

    analysis <- jmvboxClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

